mapper.update:
update t_channel
set channel = #{channel,jdbcType=VARCHAR},
source = #{source,jdbcType=VARCHAR},
typeid = #{typeid,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select
id
from t_channel
where

    category = #{category}


    appTypeId = #{appTypeId}
    and category != #{category}

order by sort
